mapper.select:
select ai.article_id as articleId,ai.article_cn_title as title,DATE_FORMAT(ai.create_time, '%Y-%m-%d') as contriTime,group_concat(aui.author_name) as authorName, ai.editor_state as editorState,ai.expert_state as expertState,ai.author_state as authorState,ai.invest_section as section from article_info ai,article_info_extend aie,author_info aui where ai.article_id=aie.article_id and ai.article_id=aui.article_id
and aie.user_id = #{userId,jdbcType=VARCHAR}
and ai.editor_state = #{editorState,jdbcType=VARCHAR}
and ai.expert_state = #{expertState,jdbcType=VARCHAR}
and ai.article_id = #{articleId,jdbcType=VARCHAR}
and ai.article_cn_title = #{articleCnTitle,jdbcType=VARCHAR}
and ai.invest_section = #{section,jdbcType=VARCHAR}
and DATE_FORMAT(ai.create_time, '%Y-%m-%d') = #{creatTime,jdbcType=VARCHAR}
and #{authorName,jdbcType=VARCHAR} in (select group_concat(aui.author_name))
group by ai.article_id,ai.article_cn_title,DATE_FORMAT(ai.create_time,'%Y-%m-%d'),ai.editor_state,ai.expert_state,ai.invest_section order by ai.create_time desc

select temp1.article_id as articleId,temp1.article_cn_title as title,DATE_FORMAT(temp1.create_time, '%Y-%m-%d') as contriTime, temp1.authorName as authorName,temp1.editor_state as editorState,temp1.expert_state as expertState,temp1.invest_section as section from ( select ai.article_id ,ai.article_cn_title ,ai.create_time,group_concat(aui.author_name) as authorName, ai.editor_state ,ai.expert_state ,ai.invest_section from article_info ai,author_info aui where ai.article_id=aui.article_id group by ai.article_id) as temp1 ,( select aie.article_id,max(if(aie.role_id='1003',aie.user_id,'')) as expertId, max(if(aie.role_id='1006',aie.user_id,'')) as editorId, max(if(aie.role_id='1001',aie.user_id,'')) as userId from article_info_extend aie group by aie.article_id) as temp2 where temp1.article_id=temp2.article_id
and temp2.expertId = #{userId,jdbcType=VARCHAR}
and temp1.editor_state = #{editorState,jdbcType=VARCHAR}
and temp1.expert_state = #{expertState,jdbcType=VARCHAR}
and temp1.article_id = #{articleId,jdbcType=VARCHAR}
and temp1.article_cn_title = #{articleCnTitle,jdbcType=VARCHAR}
and temp1.invest_section = #{section,jdbcType=VARCHAR}
and DATE_FORMAT(temp1.create_time, '%Y-%m-%d') = #{creatTime,jdbcType=VARCHAR}
and INSTR(temp1.authorName,#{authorName,jdbcType=VARCHAR})
group by temp1.article_id

select ai.article_id as articleId,ai.article_cn_title as title, ai.editor_state as editorState,ai.expert_state as expertState,ai.author_state as authorState, ai.article_en_tile as enTitle,ai.classification_nums as fNums,ai.article_cn_keywords as cnKeywords, ai.article_en_keywords as enKeywords,ai.article_cn_summary as cnSummary, ai.article_en_summary as enSummary,aai.attachment_name as attachName, aai.attachment_path as attachPath,group_concat(aui.author_name) as authorName, DATE_FORMAT(ai.create_time , '%Y-%m-%d') as contriTime ,ais.editor_download as editorDownload,ais.expert_download as expertDownload ,si.section_name as section from article_info ai,article_info_extend aie,article_attachment_info aai,author_info aui,article_info_state ais,section_info si
where si.section_id=ai.invest_section and ai.article_id=aie.article_id and ai.article_id=aai.article_id and aui.article_id=ai.article_id and ais.article_id=ai.article_id and aai.type=#{roleId,jdbcType=VARCHAR} and ai.article_id=#{articleId,jdbcType=VARCHAR}
and ai.editor_state = #{editorState,jdbcType=VARCHAR}
and ai.expert_state = #{expertState,jdbcType=VARCHAR}
and ai.author_state = #{authorState,jdbcType=VARCHAR}
and aie.user_id = #{userId,jdbcType=VARCHAR}
group by ai.article_id ,ai.article_cn_title , ai.article_en_tile ,ai.classification_nums, ai.invest_section ,ai.article_cn_keywords , ai.article_en_keywords ,ai.article_cn_summary , ai.article_en_summary ,aai.attachment_name, aai.attachment_path,ai.expert_state,ai.editor_state,ai.author_state

select auLin.article_id as articleId,auLin.article_cn_title as cnTitle,auLin.expert_state as expertState,auLin.article_cn_keywords as cnKeywords,auLin.editor_state as state,auLin.invest_section as section,auLin.create_time as createTime,auLin.authorNames as authorNames,exLin.expert_name as expertNames from ( select ari.article_id,ari.article_cn_title,ari.article_cn_keywords,ari.editor_state,ari.expert_state,ari.invest_section,ari.create_time,GROUP_CONCAT(author_name) as authorNames from article_info ari,author_info aui where ari.article_id=aui.article_id group by ari.article_id ) as auLin, (select ari.article_id,ari.article_cn_title,ari.article_cn_keywords,ari.editor_state,ari.expert_state,ari.invest_section,ari.create_time,exi.expert_name from user_info usi,expert_info exi,article_info_extend aie,article_info ari where usi.ref_id=exi.expert_id and aie.user_id=usi.user_id and ari.article_id=aie.article_id) as exLin where auLin.article_id=exLin.article_id
and auLin.invest_section= #{section,jdbcType=VARCHAR}
and INSTR(auLin.authorNames,#{authorNames,jdbcType=VARCHAR})
and DATE_FORMAT(auLin.create_time, '%Y-%m-%d')= #{createTime,jdbcType=VARCHAR}
and exLin.expert_name= #{expertNames,jdbcType=VARCHAR}
and auLin.article_cn_title= #{cnTitle,jdbcType=VARCHAR}
and auLin.article_id= #{articleId,jdbcType=VARCHAR}
and auLin.editor_state= #{state,jdbcType=VARCHAR}
and auLin.expert_state= #{expertState,jdbcType=VARCHAR}
and INSTR(auLin.article_cn_keywords,#{cnKeywords,jdbcType=VARCHAR})
order by auLin.create_time desc
