mapper.select:
select ai.article_id as articleId,ai.article_cn_title as
title,DATE_FORMAT(ai.create_time, '%Y-%m-%d') as
contriTime,group_concat(aui.author_name) as authorName,
ai.editor_state as editorState,ai.expert_state as expertState,ai.invest_section as section
from
article_info ai,article_info_extend aie,author_info aui
where
ai.article_id=aie.article_id and ai.article_id=aui.article_id

	and aie.user_id = #{userId,jdbcType=VARCHAR}


	and ai.editor_state = #{editorState,jdbcType=VARCHAR}


	and ai.expert_state = #{expertState,jdbcType=VARCHAR}


	and ai.article_id = #{articleId,jdbcType=VARCHAR}


	and ai.article_cn_title = #{articleCnTitle,jdbcType=VARCHAR}


	and ai.invest_section = #{section,jdbcType=VARCHAR}


	and DATE_FORMAT(ai.create_time, '%Y-%m-%d') = #{creatTime,jdbcType=VARCHAR}


	and #{authorName,jdbcType=VARCHAR} in (select group_concat(aui.author_name)) 

group by
ai.article_id,ai.article_cn_title,DATE_FORMAT(ai.create_time,'%Y-%m-%d'),ai.editor_state,ai.expert_state,ai.invest_section

select ai.article_id as articleId,ai.article_cn_title as title, ai.editor_state as editorState, ai.article_en_tile as enTitle,ai.classification_nums as fNums, ai.invest_section as section,ai.article_cn_keywords as cnKeywords, ai.article_en_keywords as enKeywords,ai.article_cn_summary as cnSummary, ai.article_en_summary as enSummary,aai.attachment_name as attachName, aai.attachment_path as attachPath,group_concat(aui.author_name) as authorName from article_info ai,article_info_extend aie,article_attachment_info aai,author_info aui where ai.article_id=aie.article_id and ai.article_id=aai.article_id and aui.article_id=ai.article_id and aai.type=#{roleId,jdbcType=VARCHAR} and ai.article_id=#{articleId,jdbcType=VARCHAR}
and ai.editor_state = #{editorState,jdbcType=VARCHAR}
and aie.user_id = #{userId,jdbcType=VARCHAR}
group by ai.article_id ,ai.article_cn_title , ai.article_en_tile ,ai.classification_nums, ai.invest_section ,ai.article_cn_keywords , ai.article_en_keywords ,ai.article_cn_summary , ai.article_en_summary ,aai.attachment_name, aai.attachment_path
