mapper.select:
select user_id,role_id,system_id,logon_name,refId,refName from ( select ui.user_id,ui.role_id,ui.system_id,ui.logon_name,ai.author_id as refId,ai.author_name as refName from user_info ui LEFT JOIN author_info ai on ui.ref_id=ai.author_id UNION select ui.user_id,ui.role_id,ui.system_id,ui.logon_name,ei.expert_id as refId,ei.expert_name as refName from user_info ui LEFT JOIN expert_info ei on ui.ref_id=ei.expert_id UNION select ui.user_id,ui.role_id,ui.system_id,ui.logon_name,ri.reader_id as refId,ri.reader_id as refName from user_info ui LEFT JOIN reader_info ri on ui.ref_id=ri.reader_id UNION select ui.user_id,ui.role_id,ui.system_id,ui.logon_name,edi.editor_id as refId,edi.editor_id as refName from user_info ui LEFT JOIN editor_info edi on ui.ref_id=edi.editor_id) as lin WHERE 1=1
and user_id = #{userId,jdbcType=VARCHAR}
and refId = #{refId,jdbcType=VARCHAR}
and role_id = #{roleId,jdbcType=VARCHAR}
and system_id = #{systemId,jdbcType=VARCHAR}
